package build

import (
	"os"
	"path/filepath"

	"github.com/anvil/anvil/cmd/config"
	"github.com/anvil/anvil/cmd/schema"
	"github.com/anvil/anvil/internal/generator_grpc"
	"gopkg.in/yaml.v3"
)

func Build(schemaFile string, schema *schema.Schema) {
	d, err := yaml.Marshal(schema)
	if err != nil {
		panic("fail")
	}

	path := "./anvil/" + schemaFile

	if err := os.MkdirAll(filepath.Dir(path), 0770); err != nil {
		panic(err.Error())
	}

	f, err := os.Create(path)
	if err != nil {
		panic(err.Error())
	}
	defer f.Close()

	header := []string{
		"Code generated by Anvil CLI. DO NOT EDIT.",
		"versions:",
		"- File: 1.0.0",
		"- Anvil: " + config.CLI_VERSION,
		"source: " + schemaFile,
	}

	for _, v := range header {
		_, err = f.Write([]byte("# " + v + "\n"))
		if err != nil {
			panic(err.Error())
		}
	}

	_, err = f.Write(d)
	if err != nil {
		panic(err.Error())
	}

	// Temporary, hardcoded to generate what we need for now, MVP
	err = generator_grpc.Generate(schema)
	if err != nil {
		panic(err.Error())
	}
}
